import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

function App() {
  const [art1, setArt1] = useState('');
  const [art2, setArt2] = useState('');

  const onArtistOneChange = (e) => {
    setArt1(e.target.value);

  }

  const onArtistTwoChange = (e) => {
    setArt2(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(art1);
    console.log(art2);
    const artistOne = art1;
    const artistTwo = art2;
    const data = {artistOne, artistTwo};
    const options = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    };
    fetch('http://localhost:5000/api', options)
      .then(res => res.json())
      .then(data => console.log(data))
      .catch(err => console.log(err));
  }   

  return (
    <div className="App">
      <p>Oracle of Artists</p>

      <form onSubmit={handleSubmit}>
      <label>
        Artist One: <br></br>
        <input name="artistOne" type="String" value={art1} onChange={onArtistOneChange}/>
      </label>
      <br></br>
      <label>
        Artist Two: <br></br>
        <input name="artistTwo" type="String" value={art2} onChange={onArtistTwoChange}/>
      </label>
      <br></br>
      <input type="submit" value="Submit" />
      </form>
      <div id = "container"></div>
      <footer>
        <p>Oracle of Artists</p>
      </footer>

    </div>
  );
}

export default App;
